import React, { useState } from 'react';
import './css/Sidebar.css'

const Sidebar = ({ onLogout }) => {
    const [showJobsMenu, setShowJobsMenu] = useState(false);
    const [showCompaniesMenu, setShowCompaniesMenu] = useState(false);
    const [showSkillsMenu, setShowSkillsMenu] = useState(false);

    const handleLogout = () => {
      // Implement logout logic
      onLogout();
    };

    const toggleMenu = (menu) => {
      if (menu === 'jobs') {
        setShowJobsMenu(!showJobsMenu);
      } else if (menu === 'companies') {
        setShowCompaniesMenu(!showCompaniesMenu);
      } else if (menu === 'skills') {
        setShowSkillsMenu(!showSkillsMenu);
      }
    };

  return (
    <div style={{ width: '100%', height: '100%', background: '#f0f0f0', padding: '20px' }}>
        <div style={{ height: '75px', borderBottom: '1px solid black'}}>
        <h3>Logo HERE</h3>
        </div>
      <ul style={{ listStyleType: 'none', padding: 0 }}>
        <li><a href="#home">Home</a></li>
        <li onClick={() => toggleMenu('jobs')}>
          Jobs
          {showJobsMenu && (
            <ul>
              <li><a href="#add-jobs">Add Jobs</a></li>
              <li><a href="#update-jobs">Update Jobs</a></li>
              <li><a href="#delete-jobs">Delete Jobs</a></li>
            </ul>
          )}
        </li>

        <li onClick={() => toggleMenu('companies')}>
          Companies
          {showCompaniesMenu && (
            <ul>
              <li><a href="#add-companies">Add Companies</a></li>
              <li><a href="#update-companies">Update Companies</a></li>
              <li><a href="#delete-companies">Delete Companies</a></li>
            </ul>
          )}
        </li>

        <li onClick={() => toggleMenu('skills')}>
          Skills
          {showSkillsMenu && (
            <ul>
              <li><a href="#add-skills">Add Skills</a></li>
              <li><a href="#update-skills">Update Skills</a></li>
              <li><a href="#delete-skills">Delete Skills</a></li>
            </ul>
          )}
        </li>
        <li><a href="#sort-jobs">Sort Jobs</a></li>
        <li><a href="#user-settings">User Settings</a></li>
        <li style={{ color: 'red', cursor: 'pointer' }} onClick={handleLogout} className="logout">Log Out</li>
      </ul>
    </div>
  );
};

export default Sidebar;
