import React, { useState } from 'react';
import { createCompany } from '../api/companyService';  // Make sure this API function is correctly implemented

const CreateCompany = ({ closeModal }) => {
  const [companyDetails, setCompanyDetails] = useState({
    name: '',
    industry: '',
    location: '',
    description: '',
  });

  const handleCompanySubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission behavior
    try {
      const company = await createCompany(companyDetails);
      console.log('Company created successfully:', company);
      closeModal();  // Close modal after company creation
    } catch (error) {
      console.error('Error creating company:', error);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCompanyDetails(prev => ({ ...prev, [name]: value }));
  };

  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-900">
      <form onSubmit={handleCompanySubmit} className="max-w-md w-full bg-white shadow-lg rounded-lg p-6 space-y-6">
        <h1 className="text-3xl font-semibold text-center text-blue-500">Create New Company</h1>

        <div>
          <label htmlFor="name" className="block text-sm font-medium text-gray-700">Company Name</label>
          <input
            id="name"
            name="name"
            type="text"
            value={companyDetails.name}
            onChange={handleInputChange}
            className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter company name"
          />
        </div>

        <div>
          <label htmlFor="industry" className="block text-sm font-medium text-gray-700">Industry</label>
          <input
            id="industry"
            name="industry"
            type="text"
            value={companyDetails.industry}
            onChange={handleInputChange}
            className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter industry"
          />
        </div>

        <div>
          <label htmlFor="location" className="block text-sm font-medium text-gray-700">Location</label>
          <input
            id="location"
            name="location"
            type="text"
            value={companyDetails.location}
            onChange={handleInputChange}
            className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter company location"
          />
        </div>

        <div>
          <label htmlFor="description" className="block text-sm font-medium text-gray-700">Description</label>
          <input
            id="description"
            name="description"
            type="text"
            value={companyDetails.description}
            onChange={handleInputChange}
            className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter company description"
          />
        </div>

        <button type="submit" className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Submit
        </button>
      </form>
    </div>
  );
};

export default CreateCompany;
