import React, { useState } from 'react';
import { createCompany } from '../api/companyService';

const CreateCompany = () => {
  // State for job details
  const [companyDetails, setCompanyDetails] = useState({
    name: '',
    industry: '',
    location: '',
    description: '',
  });


  // Function to handle job submission
  const handleCompanySubmit = async () => {
    try {
      const job = await createCompany(companyDetails);
      console.log('Company created successfully:', job);
      // Optionally reset the form or handle next steps
    } catch (error) {
      console.error('Error creating job:', error);
      // Optionally display an error message to the user
    }
  };

  // Function to handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setCompanyDetails({ ...companyDetails, [name]: value });
  };


  return (
    <div className="flex justify-center items-center min-h-screen bg-gray-900">
      <div className="max-w-md w-full bg-white shadow-lg rounded-lg p-6 space-y-6">
        <h1 className="text-3xl font-semibold text-center text-blue-500">New Job Entry</h1>

        {/* Company Name */}
        <div>
          <label htmlFor="name" className="block text-sm font-medium text-gray-700">Comany Name</label>
          <input
            id="name"
            name="name"
            type="text"
            value={companyDetails.name}
            onChange={handleInputChange}
            className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter company name"
          />
        </div>

        {/* Company Industry */}
        <div>
          <label htmlFor="industry" className="block text-sm font-medium text-gray-700">Company Industry</label>
          <textarea
            id="industry"
            name="industry"
            value={companyDetails.industry}
            onChange={handleInputChange}
            className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter industry"
          />
        </div>

        {/* Company Location */}
        <div>
          <label htmlFor="location" className="block text-sm font-medium text-gray-700">Company Location</label>
          <input
            id="location"
            name="location"
            type="text"
            value={companyDetails.location}
            onChange={handleInputChange}
            className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter company location"
          />
        </div>

        {/* Company Description */}
        <div>
          <label htmlFor="description" className="block text-sm font-medium text-gray-700">Company Description</label>
          <input
            id="description"
            name="description"
            type="text"
            value={companyDetails.description}
            onChange={handleInputChange}
            className="mt-1 block w-full border-gray-300 rounded-md shadow-sm focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
            placeholder="Enter company description"
          />
        </div>


        {/* Submit button */}
        <button onClick={handleJobSubmit} className="w-full bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded">
          Submit
        </button>
      </div>
    </div>
  );
};

export default CreateJob;
